// Answer Generator Request Validation
// Validates incoming request and prepares for AI processing

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const body = item.json.body || {};
    const {
      query = '',
      searchResults = [],
      userId = '',
      sessionId = '',
      conversationHistory = [],
      responseStyle = 'detailed',
      maxTokens = 1000
    } = body;

    // Validation checks
    const errors = [];

    if (!query || typeof query !== 'string' || query.trim().length === 0) {
      errors.push('Query is required and must be a non-empty string');
    }

    if (!Array.isArray(searchResults) || searchResults.length === 0) {
      errors.push('Search results are required and must be a non-empty array');
    }

    if (!userId || typeof userId !== 'string') {
      errors.push('User ID is required');
    }

    if (!sessionId || typeof sessionId !== 'string') {
      errors.push('Session ID is required');
    }

    if (errors.length > 0) {
      results.push({
        json: {
          success: false,
          error: 'Validation failed',
          details: errors,
          timestamp: new Date().toISOString()
        }
      });
      continue;
    }

    // Prepare validated data
    results.push({
      json: {
        success: true,
        validatedData: {
          query: query.trim(),
          searchResults: searchResults.slice(0, 10), // Limit to top 10 results
          userId,
          sessionId,
          conversationHistory: conversationHistory.slice(-10), // Last 10 messages
          responseStyle,
          maxTokens: Math.min(maxTokens, 2000), // Cap at 2000 tokens
          timestamp: new Date().toISOString()
        }
      }
    });

  } catch (error) {
    results.push({
      json: {
        success: false,
        error: 'Request processing failed',
        details: error.message,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;

// Context Builder for AI Response Generation
// Constructs comprehensive context from search results and conversation history

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    if (!item.json.success) {
      results.push(item);
      continue;
    }

    const { validatedData } = item.json;
    const { query, searchResults, conversationHistory, responseStyle } = validatedData;

    // Build context from search results
    const contextChunks = [];
    const citations = [];

    searchResults.forEach((result, index) => {
      const citationId = index + 1;

      // Extract key information
      const videoTitle = result.video_title || 'Unknown Video';
      const timestamp = result.timestamp || '00:00';
      const content = result.content || '';
      const relevanceScore = result.relevance_score || 0;

      // Build context chunk
      const contextChunk = `[Citation ${citationId}] From "${videoTitle}" at ${timestamp}:\n${content}`;
      contextChunks.push(contextChunk);

      // Store citation for reference
      citations.push({
        id: citationId,
        videoTitle,
        timestamp,
        relevanceScore,
        url: result.video_url || '',
        transcriptId: result.transcript_id || ''
      });
    });

    // Build conversation context
    let conversationContext = '';
    if (conversationHistory && conversationHistory.length > 0) {
      conversationContext = '\n\nRecent Conversation:\n';
      conversationHistory.forEach((msg, index) => {
        const role = msg.role || 'user';
        const content = msg.content || '';
        conversationContext += `${role}: ${content}\n`;
      });
    }

    // Combine all context
    const fullContext = contextChunks.join('\n\n');

    // Determine response style instructions
    const styleInstructions = {
      'detailed': 'Provide a comprehensive, detailed response with clear explanations and step-by-step guidance.',
      'concise': 'Keep the response brief and to-the-point while covering the essential information.',
      'beginner': 'Use simple language and explain concepts clearly for someone new to Roblox development.',
      'advanced': 'Provide technical details and assume familiarity with Roblox development concepts.'
    };

    const instruction = styleInstructions[responseStyle] || styleInstructions['detailed'];

    results.push({
      json: {
        success: true,
        aiContext: {
          query: query,
          fullContext: fullContext,
          conversationContext: conversationContext,
          citations: citations,
          styleInstruction: instruction,
          timestamp: validatedData.timestamp,
          contextLength: fullContext.length,
          citationCount: citations.length
        },
        originalData: validatedData
      }
    });

  } catch (error) {
    results.push({
      json: {
        success: false,
        error: 'Context building failed',
        details: error.message,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;

// AI Prompt Constructor for Answer Generation
// Creates optimized prompts for AI response generation

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    if (!item.json.success) {
      results.push(item);
      continue;
    }

    const { aiContext, originalData } = item.json;
    const { query, fullContext, conversationContext, styleInstruction } = aiContext;

    // Construct comprehensive AI prompt
    const systemPrompt = `You are Blox Wizard, an expert Roblox development assistant with access to comprehensive video tutorial transcripts.

Your role:
- Provide accurate, helpful answers about Roblox development
- Use ONLY the provided context from video transcripts
- Include specific citations with timestamps for all information
- ${styleInstruction}

Citation Format:
- Use format: [Citation X] for inline references
- Always include video timestamps when available
- Prefer recent or highly relevant content

Roblox Development Focus:
- Scripting (Lua programming)
- Studio tools and interface
- Game mechanics and systems
- Building and modeling techniques
- Publishing and monetization

Guidelines:
- If information isn't in the provided context, state this clearly
- For code examples, always explain the logic
- Suggest related topics when helpful
- Be encouraging and supportive for learners`;

    const userPrompt = `Question: ${query}

Available Context from Video Transcripts:
${fullContext}
${conversationContext}

Please provide a helpful answer using the provided context. Include citations and timestamps for all information used.`;

    results.push({
      json: {
        success: true,
        prompt: {
          system: systemPrompt,
          user: userPrompt,
          model: 'gpt-4o-mini',
          temperature: 0.3,
          maxTokens: originalData.maxTokens
        },
        aiContext: aiContext,
        originalData: originalData
      }
    });

  } catch (error) {
    results.push({
      json: {
        success: false,
        error: 'Prompt construction failed',
        details: error.message,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;

// Response Processor for Answer Generation
// Processes AI response and adds metadata for client consumption

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    // Get AI response from the ChatOpenAI node
    const aiResponse = item.json.text || item.json.content || '';
    const originalData = item.json.originalData || {};
    const aiContext = item.json.aiContext || {};

    if (!aiResponse) {
      results.push({
        json: {
          success: false,
          error: 'No AI response received',
          timestamp: new Date().toISOString()
        }
      });
      continue;
    }

    // Extract citation references from the response
    const citationMatches = aiResponse.match(/\[Citation \d+\]/g) || [];
    const usedCitations = citationMatches.map(match => {
      const citationId = parseInt(match.match(/\d+/)[0]);
      return aiContext.citations?.find(c => c.id === citationId);
    }).filter(Boolean);

    // Calculate response metrics
    const wordCount = aiResponse.split(/\s+/).length;
    const hasCodeExamples = /```|`[^`]+`/.test(aiResponse);
    const hasTimestamps = /\d{1,2}:\d{2}/.test(aiResponse);

    // Determine response quality score
    let qualityScore = 0.5; // Base score
    if (usedCitations.length > 0) qualityScore += 0.2;
    if (hasCodeExamples) qualityScore += 0.15;
    if (hasTimestamps) qualityScore += 0.1;
    if (wordCount > 50 && wordCount < 500) qualityScore += 0.05;
    qualityScore = Math.min(qualityScore, 1.0);

    // Build structured response
    const processedResponse = {
      answer: aiResponse,
      metadata: {
        citationsUsed: usedCitations,
        allCitations: aiContext.citations || [],
        metrics: {
          wordCount,
          citationCount: usedCitations.length,
          qualityScore: Math.round(qualityScore * 100) / 100,
          hasCodeExamples,
          hasTimestamps,
          responseTime: Date.now() - new Date(originalData.timestamp).getTime()
        },
        query: originalData.query,
        sessionId: originalData.sessionId,
        userId: originalData.userId,
        timestamp: new Date().toISOString()
      }
    };

    results.push({
      json: {
        success: true,
        response: processedResponse
      }
    });

  } catch (error) {
    results.push({
      json: {
        success: false,
        error: 'Response processing failed',
        details: error.message,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;

// Response Formatter for Answer Generation
// Final formatting and client response preparation

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    if (!item.json.success) {
      // Format error response
      results.push({
        json: {
          success: false,
          error: item.json.error || 'Unknown error',
          details: item.json.details || 'No details available',
          timestamp: item.json.timestamp || new Date().toISOString(),
          service: 'answer-generator'
        }
      });
      continue;
    }

    const { response } = item.json;

    // Format successful response for client
    const clientResponse = {
      success: true,
      data: {
        answer: response.answer,
        citations: response.metadata.citationsUsed.map(citation => ({
          id: citation.id,
          videoTitle: citation.videoTitle,
          timestamp: citation.timestamp,
          url: citation.url,
          relevanceScore: citation.relevanceScore
        })),
        metrics: {
          qualityScore: response.metadata.metrics.qualityScore,
          citationCount: response.metadata.metrics.citationCount,
          wordCount: response.metadata.metrics.wordCount,
          responseTimeMs: response.metadata.metrics.responseTime
        },
        meta: {
          sessionId: response.metadata.sessionId,
          timestamp: response.metadata.timestamp,
          service: 'answer-generator',
          version: '1.0'
        }
      }
    };

    results.push({ json: clientResponse });

  } catch (error) {
    results.push({
      json: {
        success: false,
        error: 'Response formatting failed',
        details: error.message,
        timestamp: new Date().toISOString(),
        service: 'answer-generator'
      }
    });
  }
}

return results;

// Error Handler for Answer Generation
// Comprehensive error handling and recovery guidance

const items = $input.all();
const results = [];

for (const item of items) {
  const error = item.json;

  // Determine error type and severity
  let errorType = 'unknown';
  let severity = 'medium';
  let recoveryAction = 'retry';

  const errorMessage = error.message || error.error || 'Unknown error';

  // Classify error types
  if (errorMessage.includes('validation') || errorMessage.includes('required')) {
    errorType = 'validation_error';
    severity = 'low';
    recoveryAction = 'fix_input';
  } else if (errorMessage.includes('timeout') || errorMessage.includes('network')) {
    errorType = 'network_error';
    severity = 'medium';
    recoveryAction = 'retry_with_backoff';
  } else if (errorMessage.includes('rate limit') || errorMessage.includes('quota')) {
    errorType = 'rate_limit_error';
    severity = 'high';
    recoveryAction = 'wait_and_retry';
  } else if (errorMessage.includes('unauthorized') || errorMessage.includes('authentication')) {
    errorType = 'auth_error';
    severity = 'high';
    recoveryAction = 'check_credentials';
  } else if (errorMessage.includes('ai') || errorMessage.includes('openai')) {
    errorType = 'ai_service_error';
    severity = 'high';
    recoveryAction = 'fallback_model';
  }

  // Recovery suggestions
  const recoveryGuide = {
    'fix_input': 'Check request validation requirements and fix input parameters',
    'retry_with_backoff': 'Retry request with exponential backoff delay',
    'wait_and_retry': 'Wait for rate limit reset period before retrying',
    'check_credentials': 'Verify API keys and authentication configuration',
    'fallback_model': 'Try alternative AI model or cached response',
    'retry': 'Simple retry with same parameters'
  };

  const errorResponse = {
    success: false,
    error: {
      type: errorType,
      severity: severity,
      message: errorMessage,
      timestamp: new Date().toISOString(),
      service: 'answer-generator',
      recovery: {
        action: recoveryAction,
        guidance: recoveryGuide[recoveryAction],
        retryable: ['retry', 'retry_with_backoff', 'wait_and_retry'].includes(recoveryAction)
      }
    }
  };

  results.push({ json: errorResponse });
}

return results;
