// ðŸ“Š RESPONSE PROCESSOR
// Formats AI response with citations and metadata

const aiResponse = $input.first().json;
const originalRequest = $('Request Validator').first().json;

console.log('ðŸ“Š Processing AI response');

// Extract AI answer
const aiAnswer = aiResponse.choices?.[0]?.message?.content || 'I apologize, but I could not generate a response. Please try again.';

// Process citations from search results
const citations = (originalRequest.searchResults || []).map((result, index) => ({
  id: index + 1,
  videoTitle: result.video_title || 'Unknown Video',
  timestamp: result.timestamp || '0:00',
  url: result.youtube_id ? `https://youtube.com/watch?v=${result.youtube_id}` : '#',
  relevanceScore: result.similarity_score || 0.8,
  snippet: result.chunk_text?.substring(0, 100) + '...' || ''
}));

// Generate suggested follow-up questions
function generateSuggestedQuestions(query, responseStyle) {
  const style = responseStyle || 'detailed';
  const lowerQuery = query.toLowerCase();

  if (lowerQuery.includes('script') || lowerQuery.includes('code')) {
    return [
      'How do I debug this script?',
      'What are common scripting mistakes?', 
      'Can you show me more advanced techniques?',
      'How do I optimize this code?'
    ];
  }

  if (lowerQuery.includes('build') || lowerQuery.includes('create')) {
    return [
      'What tools do I need for this?',
      'Are there any shortcuts?',
      'How do I make this more efficient?',
      'What are best practices?'
    ];
  }

  // Default suggestions
  return [
    'Can you explain this in more detail?',
    'What should I learn next?',
    'Are there related tutorials?',
    'How do I practice this?'
  ];
}

// Create structured response
const structuredResponse = {
  success: true,
  answer: aiAnswer,
  citations: citations,
  videoReferences: citations.map(c => ({
    title: c.videoTitle,
    youtubeId: extractYouTubeId(c.url),
    timestamp: c.timestamp,
    relevantSegment: c.snippet,
    confidence: c.relevanceScore
  })),
  suggestedQuestions: generateSuggestedQuestions(originalRequest.query, originalRequest.responseStyle),

  // Response metadata
  responseMetadata: {
    model: 'gpt-4o-mini',
    temperature: 0.3,
    tokensUsed: aiResponse.usage?.total_tokens || 0,
    responseStyle: originalRequest.responseStyle,
    processingTime: Date.now() - new Date(originalRequest.timestamp).getTime(),
    requestId: originalRequest.requestId
  },

  timestamp: new Date().toISOString()
};

// Helper function to extract YouTube ID
function extractYouTubeId(url) {
  const match = url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\n?#]+)/);
  return match ? match[1] : null;
}

console.log(`âœ… Response processed with ${citations.length} citations`);

return [{ json: structuredResponse }];
